mapper.insert:
insert into review
	(
	user_id             
	, info_num                  
	, review_num                
	, review_title      
	, review_content        
	)
	values
	(
	#{user_id}
	,#{info_num}
	,review_seq.nextval
	,#{review_title}
	,#{review_content}
	)

mapper.update:
update
	review
set
	review_title=#{review_title},	
	review_content=#{review_content}
where
	review_num = #{review_num}
and
	user_id = #{user_id}
