mapper.insert:
insert into 
    web5_board
     (
          boardnum
          ,id 
          ,title 
          ,contents

          ,originalfile
          ,savedfile

      )
  values
      (
           web5_board_seq.nextval
           ,#{id}
           ,#{title}
           ,#{contents}

           ,#{originalfile}
           ,#{savedfile}

       )

mapper.update:
update 
      web5_board
 set 
      title = #{title},
      contents = #{contents}
 where
      boardnum = #{boardnum} 
  and
      id= #{id}

mapper.insert:
insert into web5_reply(replynum, boardnum, id, text)
       values(web5_reply_seq.nextval,
              #{boardnum},
              #{id},
              #{text}
              )

mapper.delete:
delete 
      from 
           web5_reply 
       where 
           replynum = #{replynum} 
           and id = #{id}
