mapper.insert:
insert into 
  notice 
  ( 
     admin_id
   , notice_num
   , notice_title
   , notice_content

   )
  values
   (
     #{admin_id}
     , #{notice_num}
     , #{notice_title}
     , #{notice_content}
    )

insert into 
     notice_pic
     (
      noticepic_num

      , savedfile

      , notice_num
     )
  values
    (
     notice_pic_seq.nextval

     ,#{savedfile}

     , #{notice_num}
    )

mapper.update:
update
      notice
  set
      notice_title = #{notice_title}
      , notice_content = #{notice_content}
  where
      notice_num = #{notice_num}
  and
      admin_id = #{admin_id}

update
     notice_pic
 set
     noticepic_num = #{noticepic_num}
     , savedfile = #{savedfile}
 where 
     notice_num = #{notice_num}
