mapper.insert:
insert into
	tourinfo
	(info_num
	, admin_id
	, info_title
	, info_subtitle
	, info_name
	, info_gu
	, info_address
	, info_content
	, info_state
	, savedfile
	)
values
	(tourinfo_seq.nextval
	, #{admin_id}
	, #{info_title}
	, #{info_subtitle}
	, #{info_name}
	, #{info_gu}
	, #{info_address}
	, #{info_content}
	, #{info_state}
	, #{savedfile}
	)

mapper.update:
update
	tourinfo
set
	info_title = #{info_title}
	, info_subtitle = #{info_subtitle}
	, info_name = #{info_name}
	, info_gu = #{info_gu}
	, info_address = #{info_address}
	, info_content = #{info_content}
	, info_state = #{info_state}
	, savedfile = #{savedfile}
where
	info_num = #{info_num}
	and admin_id = #{admin_id}
